parameters.jsCode:
const item = $input.first();
const profile = item.json.body?.profile;
let profileContext = '';
if (profile) {
  const lines = [
    '## AUTOR-PROFIL & STIMME',
    `Schreibe als ${profile.full_name}, ${profile.job_title} bei ${profile.company}.`,
    `Expertise: ${(profile.expertise_topics || []).join(', ')}`,
    `Zielgruppe: ${profile.target_audience || ''}`,
    `Werte: ${(profile.personal_values || []).join(', ')}`,
    `Positionierung: ${profile.positioning_statement || ''}`,
    `Emoji-Stil: ${profile.preferred_emojis || 'minimal'}`,
  ];
  if (profile.example_posts?.length > 0) {
    lines.push('', '## STIL-BEISPIELE (passe Voice & Rhythmus an):');
    lines.push(profile.example_posts.join('\n---\n'));
  }
  lines.push('', 'WICHTIG: Schreibe in der Ich-Perspektive als diese Person.', '');
  profileContext = lines.join('\n');
}
return [{ json: { ...item.json, profileContext } }];
